    private void jButton_RegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_RegistrarActionPerformed
        //añadir usuario
        if (jTextField_nombre.getText().equals("Ingrese su nombre")
                || jTextField_apellido.getText().equals("Ingrese su apellido")
                || jTextField_usuario.getText().equals("Ingrese un nombre de usuario")
                || jPasswordField_contraseña.getText().equals("********")
                || jTextField_nombre.getText().equals("")
                || jTextField_apellido.getText().equals("")
                || jTextField_usuario.getText().equals("")
                || jPasswordField_contraseña.getText().equals("")) {
            JOptionPane.showMessageDialog(null, "Hay campos vacios");

        } else {
            //si ya esta registrado no se guarda
            if (metodos.existeUsuario(jTextField_usuario.getText()) == 0) {//si es 0 no existe
                int i = metodos.guardar(jTextField_nombre.getText(), jTextField_apellido.getText(), jTextField_usuario.getText(), jPasswordField_contraseña.getText());
                if (i > 0) {//si i tiene parametros añadidos
                    JOptionPane.showMessageDialog(this, "Datos registrados en el sistema");
                } else {
                    JOptionPane.showMessageDialog(this, "No se pudo registrar el usuario nuevo");
                }
            } else {
                JOptionPane.showMessageDialog(null, "Este usuario ya existe, digite otro nombre de usuario.");
            }
        }
        /*JOptionPane.showMessageDialog(null, "Este usuario ya a sido registrado");*/
    }//GEN-LAST:event_jButton_RegistrarActionPerformed

