    private void jPasswordField_contraseñaMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jPasswordField_contraseñaMousePressed

        if (String.valueOf(jPasswordField_contraseña.getPassword()).equals("********")) {
            jPasswordField_contraseña.setText("");
            jPasswordField_contraseña.setForeground(Color.black);
        }
        if (jTextField_nombre.getText().isEmpty()) {
            jTextField_nombre.setText("Ingrese su nombre");
            jTextField_nombre.setForeground(Color.gray);
        }

        if (jTextField_apellido.getText().isEmpty()) {
            jTextField_apellido.setText("Ingrese su apellido");
            jTextField_apellido.setForeground(Color.gray);
        }

        if (jTextField_usuario.getText().isEmpty()) {
            jTextField_usuario.setText("Ingrese un nombre de usuario");
            jTextField_usuario.setForeground(Color.gray);
        }

    }//GEN-LAST:event_jPasswordField_contraseñaMousePressed

