    private void jButton_entrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_entrarActionPerformed
        String busqueda_uResgistrado = metodos.BuscarUsuarioRegistrado(jTextField_usuario.getText(), jPasswordField_contraseña.getText());
        if(jTextField_usuario.getText().equals("root")&&jPasswordField_contraseña.getText().equals("root")){
            JOptionPane.showMessageDialog(null, "Iniciaste como Admin");
            
            Frm_sistema ventana = new Frm_sistema();//aca le deberia meter la contra(jPasswordField_contraseña.getText())
            ventana.jLabel_nombreSistema.setText("Eres Admin");
            ventana.setVisible(true);
            
            this.dispose();
            
        }else if(busqueda_uResgistrado.equals("Usuario registrado")){
            //Quepasa si estoy en la base de datos
            String busqueda_usuario = metodos.buscarUsuario(jTextField_usuario.getText());
            
            String user = jTextField_usuario.getText();
            
            JOptionPane.showMessageDialog(this, "Bienvenido(a)"+busqueda_usuario);
            
            Frm_sistema ventana = new Frm_sistema();//aca le deberia meter la contra
            
            
            ventana.jLabel_nombreSistema.setText(busqueda_usuario);//colocamos nombre en label
            
            //Usar el id y mostrarlo
            String busqueda_id = metodos.buscarId(jTextField_usuario.getText());
            
            ventana.jLabel_idUsuario.setText(busqueda_id);
            ventana.jLabel_user.setText(user);
                    
            ventana.setVisible(true);
            
            
            this.dispose();
        }else{
            JOptionPane.showMessageDialog(null, "No estas registrado, por favor registrate");
        }
        
    }//GEN-LAST:event_jButton_entrarActionPerformed

