    private void jTextField_usuarioMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTextField_usuarioMousePressed
       if(jTextField_usuario.getText().equals("Ingrese su nombre de usuario")){
          jTextField_usuario.setText("");
          jTextField_usuario.setForeground(Color.black);
       }
       if(String.valueOf(jPasswordField_contraseña.getPassword()).isEmpty()){
          jPasswordField_contraseña.setText("********");
          jPasswordField_contraseña.setForeground(Color.gray);
       }
       
    }//GEN-LAST:event_jTextField_usuarioMousePressed

    private void jPasswordField_contraseñaMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jPasswordField_contraseñaMousePressed
       if(String.valueOf(jPasswordField_contraseña.getPassword()).equals("********")){
           jPasswordField_contraseña.setText("");
           jPasswordField_contraseña.setForeground(Color.black);
       }
       if(jTextField_usuario.getText().isEmpty()){
           jTextField_usuario.setText("Ingrese su nombre de usuario");
           jTextField_usuario.setForeground(Color.gray);
       }
    
    }//GEN-LAST:event_jPasswordField_contraseñaMousePressed

